    public static int[][] restoreMatrix(int[] rowSum, int[] colSum) {
        long[][] res = new long[rowSum.length][colSum.length];
        int[][] result = new int[rowSum.length][colSum.length];
        int x = rowSum.length;
        int y = colSum.length;
        for (int i = 0; i < x; i++) {
            res[i][0] = rowSum[i];
        }
        int left = 0;
        int right = y - 1;
        while (left < right) {
            int leftSum = colSum[left];
            int rightSum = colSum[right];
            long curLeftSum = 0;
            long curRightSum = 0;
            for (long[] re : res) {
                curLeftSum += re[left];
                curRightSum += re[right];
            }
            int index = 0;
            if (curLeftSum > leftSum && curRightSum < rightSum) {
                long t = (rightSum - curRightSum);
                while (t > 0) {
                    if (t < res[index][left]) {
                        res[index][left] -= t;
                        res[index][right] += t;
                        t = 0;
                    } else {
                        long i = res[index][left];
                        res[index][left] = 0;
                        res[index][right] += i;
                        t -= i;
                    }
                    index++;
                }
            }
            right--;
        }
        for (int i = 0; i <res.length; i++) {
            for (int j = 0; j <res[i].length ; j++) {
                result[i][j]= (int) res[i][j];
            }
        }
        return result;
    }

