    public long repairCars(int[] ranks, int cars) {
        Arrays.sort(ranks);
        long left = 1;
        long right = 99999999999999L;
        while (left <= right) {
            long mid = left + (right - left) / 2;
            boolean check = check(mid, ranks,cars);
            if (check) {
                right =  (mid - 1);
            } else {
                left =  (mid + 1);

            }
        }
        return left;
    }

    private boolean check(long mid, int[] ranks, int cars) {
        long count = 0;
        for (int x : ranks) {
            long l = mid / x;
            double sqrt = Math.sqrt(l);
            long val = (int) sqrt;
            count += val;
            if(count >= cars){
                return true;
            }
        }
        return count >= cars;
    }
